def encontrar_elementos_comunes(arr1, arr2):
    elementos_comunes = []
    for i in range(len(arr1)):
        for j in range(len(arr2)):
            if arr1[i] == arr2[j]:
                # Verificar si el elemento ya está en la lista de elementos_comunes
                if arr1[i] not in elementos_comunes:
                    elementos_comunes.append(arr1[i])
    return elementos_comunes
