```cpp
#include<iostream>
using namespace std;

int fibonacci(int n) {
   if((n==1)||(n==0)) {
      return(n);
   }else {
      return(fibonacci(n-1)+fibonacci(n-2));
   }
}

int main() {
   int n;
   cout << "Entrez le nombre de termes de la série de Fibonacci à afficher: ";
   cin >> n;
   cout << "Le " << n << "ème nombre de la série de Fibonacci est: " << fibonacci(n) << endl;
   return 0;
}
```

