def majority_element(arr):
    count = 0
    candidate = None
    # Boyer-Moore投票アルゴリズム
    for num in arr:
        if count == 0:
            candidate = num
        count += (1 if num == candidate else -1)
    # 候補が本当に過半数の要素であるか確認
    if arr.count(candidate) > len(arr) // 2:
        return candidate
    else:
        return None
