def vind_kde_element_helper(lijst1, lijst2, k):
    if len(lijst1) > len(lijst2):
        return vind_kde_element_helper(lijst2, lijst1, k)
    if not lijst1:
        return lijst2[k - 1]
    if k == 1:
        return min(lijst1[0], lijst2[0])
    i = min(len(lijst1), k // 2)
    j = k - i
    if lijst1[i - 1] < lijst2[j - 1]:
        return vind_kde_element_helper(lijst1[i:], lijst2, k - i)
    else:
        return vind_kde_element_helper(lijst1, lijst2[j:], k - j)
def kde_kleinste_element(lijst1, lijst2, k):
    return vind_kde_element_helper(lijst1, lijst2, k)
