import os
import re
from collections import Counter
import concurrent.futures
def get_files_in_directory(diretorio):
    return [os.path.join(diretorio, f) for f in os.listdir(diretorio) if os.path.isfile(os.path.join(diretorio, f)) and f.endswith('.txt')]
def read_file(caminho_arquivo):
    with open(caminho_arquivo, 'r', encoding='utf-8') as arquivo:
        return arquivo.read()
def count_words(texto):
    palavras = re.findall(r'\w+', texto.lower())
    return Counter(palavras)
def process_file(arquivo):
    texto = read_file(arquivo)
    return count_words(texto)
def main():
    diretorio = input("Digite o caminho do diretório: ")
    arquivos = get_files_in_directory(diretorio)
    contagem_palavras = Counter()
    with concurrent.futures.ThreadPoolExecutor() as executor:
        futuras_contagens_palavras = {executor.submit(process_file, arquivo): arquivo for arquivo in arquivos}
        for futuro in concurrent.futures.as_completed(futuras_contagens_palavras):
            contagem_palavras += futuro.result()
    top_5_palavras = contagem_palavras.most_common(5)
    print("As 5 palavras com o maior número de ocorrências:")
    for palavra, contagem in top_5_palavras:
        print(f"{palavra}: {contagem}")
if __name__ == "__main__":
    main()
```

