def encontrarMediana(nums1, nums2):
    total = len(nums1) + len(nums2)
    if total % 2 == 1:
        return encontrarKth(nums1, nums2, total // 2 + 1)
    else:
        return (encontrarKth(nums1, nums2, total // 2) + encontrarKth(nums1, nums2, total // 2 + 1)) / 2.0
def encontrarKth(nums1, nums2, k):
    if len(nums1) > len(nums2):
        nums1, nums2 = nums2, nums1
    if not nums1:
        return nums2[k-1]
    if k == 1:
        return min(nums1[0], nums2[0])
    i = min(k // 2, len(nums1))
    j = k - i
    if nums1[i-1] <= nums2[j-1]:
        return encontrarKth(nums1[i:], nums2, j)
    else:
        return encontrarKth(nums1, nums2[j:], i)
