def arvores_binarias_completas(n):
    if n % 2 == 0:
        return 0
    if n == 1:
        return 1

    dp = [0] * (n + 1)
    dp[1] = 1

    for i in range(3, n + 1, 2):
        for j in range(1, i - 1, 2):
            dp[i] += dp[j] * dp[i - j - 1]

    return dp[n]
