```cpp
#include<iostream>
using namespace std;

int sequence(int n) {
   if(n==0) {
      return 0;
   } else if(n==1 || n==2) {
      return -1;
   } else {
      return sequence(n-1) + sequence(n-2) + sequence(n-3);
